	]
}

{
	"title": "Fast Geometric Projections for Local Robustness Certification",
	"authors": [
		"Aymeric Fromherz",
		"Klas Leino",
		"Matt Fredrikson",
		"Bryan Parno", 
		"Corina Păsăreanu"
